import React, { useState } from 'react';
import Button from 'react-bootstrap/Button';
import Col from 'react-bootstrap/Col';
import Form from 'react-bootstrap/Form';
import InputGroup from 'react-bootstrap/InputGroup';
import Row from 'react-bootstrap/Row';
import {FloatingLabel} from "react-bootstrap";




function AddDevice({onAdd}, items) {
    // Создаем состояния для хранения введенных данных
    const [name, setName] = useState('');
    const [ipAddress, setIPAddress] = useState('');
    // Создаем состояние для хранения массива данных
    const [data, setData] = useState([]);
    const [ipError, setIpError] = useState(''); // Состояние для хранения сообщения об ошибке


    // Обработчик события при отправке формы
    const handleSubmit = (event) => {
        event.preventDefault();

        const newData = {
            name: name,
            ipAddress: ipAddress
        };

        const isIPExist = items.some((item) => item.ipAddress === ipAddress);
        const localStorageItems = JSON.parse(localStorage.getItem('items')) || [];
        const isLocalStorageIPExist = localStorageItems.some((item) => item.ipAddress === ipAddress);

        if (isIPExist || isLocalStorageIPExist) {
            setIpError('IP-адрес уже существует');
            return;
        }

        onAdd(newData);
        setData([...data, newData]);
        setName('');
        setIPAddress('');
        setIpError('');

        const isValidIP = validateIPAddress(ipAddress);

        if (!isValidIP) {
            setIpError('Неправильный формат IP-адреса');
            return;
        }
    };



    // Функция для проверки правильности формата IP-адреса
    const validateIPAddress = (ip) => {
        const ipPattern = /^([0-9]{1,3}\.){3}[0-9]{1,3}$/;
        return ipPattern.test(ip);
    };

    return (
        <Form>
            <Row>
                <Col xs="auto">
                    <Form.Label htmlFor="name" visuallyHidden>
                        Name
                    </Form.Label>
                    <Form.Control
                        type="text"
                        id="name"
                        className="mb-2 "
                        value={name}
                        onChange={(e) => setName(e.target.value)}
                        placeholder="Имя"
                    />
                </Col>
                <Col xs="auto">
                    <Form.Label htmlFor="ipAdress" visuallyHidden>
                        IP
                    </Form.Label>
                    <InputGroup className="mb-2">

                        <Form.Control
                            className={`mb-2 ${ipError ? 'is-invalid' : ''}`}
                            type="ip"
                            id="ipAdress"
                            value={ipAddress}
                            onChange={(e) => setIPAddress(e.target.value)}
                            placeholder="IP-Адрес" />
                        <Form.Control.Feedback type="invalid">
                            Неверный формат IP-адреса
                        </Form.Control.Feedback>
                    </InputGroup>
                </Col>

                <Col xs="auto">
                    <Button type="submit" className="mb-2" onClick={handleSubmit}>
                        Добавить
                    </Button>
                </Col>
            </Row>
        </Form>
    );
}

export default AddDevice;
