import { useState } from 'react';
import Card from 'react-bootstrap/Card';
import { ButtonGroup, ToggleButton } from 'react-bootstrap';

function ServerItem({ item, buttonNames }) {
    const { name, ipAddress, port } = item;
    const [radioValue, setRadioValue] = useState('');


    const radios = [
        { name: 'Порт 1', value: buttonNames[0] },
        { name: 'Порт 2', value: buttonNames[1] },
        { name: 'Порт 3', value: buttonNames[2] },
    ];

    return (
        <Card style={{ width: '31rem' }}>
            <Card.Title className="m-3">{name}</Card.Title>
            <Card.Subtitle className="mx-3">
                {ipAddress}:{port}
            </Card.Subtitle>
            <Card.Body>
                <ButtonGroup>
                    {radios.map((radio, idx) => (
                        <ToggleButton
                            key={idx}
                            id={`radio-${idx}`}
                            type="radio"
                            variant="outline-success"
                            name="radio"
                            value={radio.value}
                            checked={radioValue === radio.value}
                            onChange={(e) => setRadioValue(e.currentTarget.value)}
                        >
                            {radio.name}
                        </ToggleButton>
                    ))}
                </ButtonGroup>
            </Card.Body>
        </Card>
    );
}

export default ServerItem;
