import React, { useContext, useState } from 'react';
import Card from 'react-bootstrap/Card';
import Button from 'react-bootstrap/Button';
import ServerContext from './ServerContext';
import { Col, Row } from 'react-bootstrap';
import EditServerForm from "./EditServerForm";

function ServerList() {
    const { servers, deleteServer, editServer } = useContext(ServerContext);
    const [editServerId, setEditServerId] = useState(null);

    const handleDelete = (server) => {
        deleteServer(server);
    };

    const handleEdit = (serverId) => {
        setEditServerId(serverId);
    };

    return (
        <Row xs={1} md={2} lg={4} className="g-4">
            {servers.map((server, index) => (
                <Col key={index}>
                    <Card key={server.ipAddress} style={{ high: '30rem', width: '20rem', marginRight: '10px' }}>
                        <Card.Body>
                            <Card.Title>{server.name}</Card.Title>
                            <Card.Text>
                                <strong>IP Address:</strong> {server.ipAddress}
                                <br />
                                <strong>Port:</strong> {server.port}
                            </Card.Text>
                            <Button className="me-1" variant="outline-danger" onClick={() => handleDelete(server)}>
                                Удалить
                            </Button>
                            <Button className="me-1" variant="outline-primary" onClick={() => handleEdit(server.id)}>
                                Изменить
                            </Button>
                           
                        </Card.Body>
                    </Card>
                </Col>
            ))}
        </Row>
    );
}

export default ServerList;
