import {useState} from "react";
import React from 'react';
import Card from 'react-bootstrap/Card';
import {ButtonGroup} from "react-bootstrap";
import {ToggleButton} from "react-bootstrap";
import '../Styles/ServerList.css';

function ServerItem(/*{ item }*/) {
    /*const { name, ipAddress, port } = item;*/
    const [radioValue, setRadioValue] = useState('1');
    const radios = [
        { name: 'Порт 1', value: '1' },
        { name: 'Порт 2', value: '2' },
        { name: 'Порт 3', value: '3' },
        { name: 'Порт 4', value: '4' },
        { name: 'Порт 5', value: '5' },
        { name: 'Порт 6', value: '6' },
        ];
    return (
        <Card style={{ width: 'auto' }} className="align-items-center">
            <Card.Title>Коммутатор 1</Card.Title>
            <Card.Subtitle>191.168.1.1:3000</Card.Subtitle>
            <Card.Body>
                <ButtonGroup>
                    {radios.map((radio, idx) => (
                        <ToggleButton
                            key={idx}
                            id={`radio-${idx}`}
                            type="radio"
                            variant={idx % 2 ? 'outline-success' : 'outline-danger'}
                            name="radio"
                            value={radio.value}
                            checked={radioValue === radio.value}
                            onChange={(e) => setRadioValue(e.currentTarget.value)}
                        >
                            {radio.name}
                        </ToggleButton>
                    ))}
                </ButtonGroup>
            </Card.Body>
        </Card>
    );
}

export default ServerItem;
