import React, { useState } from 'react';
import Card from 'react-bootstrap/Card';
import ToggleButton from 'react-bootstrap/ToggleButton';
import ButtonGroup from 'react-bootstrap/ButtonGroup';

function ServerItem({ item }) {
    const [activeButton, setActiveButton] = useState(null);

    const handleButtonClick = (buttonValue) => {
        setActiveButton(buttonValue);
        // Здесь можно добавить логику для обработки нажатия кнопки
        console.log(`Нажата кнопка ${buttonValue} для элемента ${item.name}`);
    };

    return (
        <Card>
            <Card.Body>
                <Card.Title>{item.name}</Card.Title>
                <Card.Text>
                    IP адрес: {item.ipAddress}
                    <br />
                    Порт: {item.port}
                </Card.Text>
                <ButtonGroup toggle className="mb-2">
                    <ToggleButton
                        type="checkbox"
                        variant="primary"
                        value={1}
                        checked={activeButton === 1}
                        onChange={() => handleButtonClick(1)}
                    >
                        Кнопка 1
                    </ToggleButton>
                    <ToggleButton
                        type="checkbox"
                        variant="primary"
                        value={2}
                        checked={activeButton === 2}
                        onChange={() => handleButtonClick(2)}
                    >
                        Кнопка 2
                    </ToggleButton>
                    <ToggleButton
                        type="checkbox"
                        variant="primary"
                        value={3}
                        checked={activeButton === 3}
                        onChange={() => handleButtonClick(3)}
                    >
                        Кнопка 3
                    </ToggleButton>
                    <ToggleButton
                        type="checkbox"
                        variant="primary"
                        value={4}
                        checked={activeButton === 4}
                        onChange={() => handleButtonClick(4)}
                    >
                        Кнопка 4
                    </ToggleButton>
                    <ToggleButton
                        type="checkbox"
                        variant="primary"
                        value={5}
                        checked={activeButton === 5}
                        onChange={() => handleButtonClick(5)}
                    >
                        Кнопка 5
                    </ToggleButton>
                    <ToggleButton
                        type="checkbox"
                        variant="primary"
                        value={6}
                        checked={activeButton === 6}
                        onChange={() => handleButtonClick(6)}
                    >
                        Кнопка 6
                    </ToggleButton>
                </ButtonGroup>
            </Card.Body>
        </Card>
    );
}

export default ServerItem;
