import React, { useState } from 'react';
import Button from 'react-bootstrap/Button';
import Col from 'react-bootstrap/Col';
import Form from 'react-bootstrap/Form';
import InputGroup from 'react-bootstrap/InputGroup';
import Row from 'react-bootstrap/Row';




function AddDevice() {
    // Создаем состояния для хранения введенных данных
    const [name, setName] = useState('');
    const [ipAddress, setIPAddress] = useState('');
    // Создаем состояние для хранения массива данных
    const [data, setData] = useState([]);

    // Обработчик события при отправке формы
    const handleSubmit = (event) => {
        event.preventDefault(); // Предотвращаем перезагрузку страницы

        // Создаем новый объект с введенными данными
        const newData = {
            name: name,
            ipAddress: ipAddress
        };

        // Добавляем новый объект в массив данных
        setData([...data, newData]);

        // Сбрасываем значения полей ввода
        setName('');
        setIPAddress('');
    };
    return (
        <Form>
            <Row className="align-items-center">
                <Col xs="auto">
                    <Form.Label htmlFor="inlineFormInput" visuallyHidden>
                        Name
                    </Form.Label>
                    <Form.Control
                        className="mb-2"
                        type="name"
                        value={FormData.name}
                        onChange={handleInputChange}
                        id="inlineFormInput"
                        placeholder="Имя"
                    />
                </Col>
                <Col xs="auto">
                    <Form.Label htmlFor="inlineFormInputGroup" visuallyHidden>
                        IP
                    </Form.Label>
                    <InputGroup className="mb-2">
                        <Form.Control
                            id="inlineFormInputGroup"
                            type="ip"
                            value={FormData.ip}
                            onChange={handleInputChange}
                            placeholder="IP-адрес" />
                    </InputGroup>
                </Col>

                <Col xs="auto">
                    <Button type="submit" className="mb-2" onClick={handleSubmit}>
                        Добавить
                    </Button>
                </Col>
            </Row>
        </Form>
    );
}

export default AddDevice;
