import React, { useContext } from 'react';
import Card from 'react-bootstrap/Card';
import Button from 'react-bootstrap/Button';
import ServerContext from './ServerContext';
import {Col, Row} from "react-bootstrap"

function ServerList() {
    const { servers, deleteServer, editServer } = useContext(ServerContext);

    return (
        <Row xs={1} md={2} lg={4} className="g-4">
            {servers.map((server, index) => (
                <Col key={index}>
                    <Card key={server.ipAddress} style={{ high:'30rem', width: '20rem', marginRight: '10px' }}>
                        <Card.Body>
                            <Card.Title>{server.name}</Card.Title>
                            <Card.Text>
                                <strong>IP Address:</strong> {server.ipAddress}
                                <br />
                                <strong>Port:</strong> {server.port}
                            </Card.Text>
                            <Button variant="danger" onClick={() => deleteServer(server)}>
                                Удалить
                            </Button>
                            <Button variant="primary" onClick={() => editServer(server)}>
                                Изменить
                            </Button>
                        </Card.Body>
                    </Card>
                </Col>
            ))}
        </Row>
    );
}

export default ServerList;
