import {useState} from "react";
import React from 'react';
import Card from 'react-bootstrap/Card';
import {ButtonGroup} from "react-bootstrap";
import {ToggleButton} from "react-bootstrap";
import '../Styles/ServerList.css';

function ServerItem({ item, buttonNames }) {
    const { name, ipAddress, port } = item;
    const [radioValue, setRadioValue] = useState(buttonNames[0]);
    const radios = buttonNames.map((name, index) => ({ ind: name, value: `${index}` }));

    return (
        <Card style={{ width: '31rem' }}>
            <Card.Title className="m-3">{item.name}</Card.Title>
            <Card.Subtitle className="mx-3">{item.ipAddress}:{item.port}</Card.Subtitle>
            <Card.Body>
                <ButtonGroup>
                    {radios.map((radio, idx) => (
                        <ToggleButton
                            key={idx}
                            id={`radio-${idx}`}
                            type="radio"
                            variant="outline-success"
                            name="radio"
                            value={radio.value}
                            checked={radioValue === radio.value}
                            onChange={(e) => setRadioValue(e.currentTarget.value)}
                            disabled={false} // Установите disabled в false, чтобы активировать все кнопки
                        >
                            {radio.ind}
                        </ToggleButton>
                    ))}
                </ButtonGroup>
            </Card.Body>
        </Card>
    );
}

export default ServerItem;
