import React from 'react';
import Card from 'react-bootstrap/Card';
import Button from 'react-bootstrap/Button';

function ServerList({ servers, onDelete, onEdit }) {
    return (
        <div className="card-list">
            {servers.map((server) => (
                <Card key={server.ipAddress} style={{ width: '18rem', marginRight: '10px' }}>
                    <Card.Body>
                        <Card.Title>{server.name}</Card.Title>
                        <Card.Text>
                            <strong>IP Address:</strong> {server.ipAddress}
                            <br />
                            <strong>Port:</strong> {server.port}
                        </Card.Text>
                        <Button variant="danger" onClick={() => onDelete(server)}>
                            Delete
                        </Button>
                        <Button variant="primary" onClick={() => onEdit(server)}>
                            Edit
                        </Button>
                    </Card.Body>
                </Card>
            ))}
        </div>
    );
}

export default ServerList;
