import React, { useState } from 'react';
import Card from 'react-bootstrap/Card';
import Button from 'react-bootstrap/Button';
import ButtonGroup from 'react-bootstrap/ButtonGroup';
import axios from 'axios';

function ServerItem({ item }) {
    const [activeButton, setActiveButton] = useState(null);

    const handleButtonClick = async (buttonIndex) => {
        if (buttonIndex === activeButton) {
            setActiveButton(null);
        } else {
            if (activeButton !== null) {
                // Отключаем предыдущую активную кнопку
                const previousButton = document.getElementById(`button-${activeButton}`);
                if (previousButton) {
                    previousButton.classList.remove('active');
                }
            }

            // Активируем выбранную кнопку
            const currentButton = document.getElementById(`button-${buttonIndex}`);
            if (currentButton) {
                currentButton.classList.add('active');
            }

            try {
                const response = await axios.get(`http://${item.ipAddress}:${item.port}/api/${buttonIndex}`);
                console.log(`Отправлен гет-запрос для элемента ${item.name}, кнопка ${buttonIndex}`);
                console.log('Ответ:', response.data);
            } catch (error) {
                console.error('Ошибка при выполнении гет-запроса:', error);
            }

            setActiveButton(buttonIndex);
        }
    };

    return (
        <Card>
            <Card.Body>
                <Card.Title>{item.name}</Card.Title>
                <Card.Text>
                    IP адрес: {item.ipAddress}
                    <br />
                    Порт: {item.port}
                </Card.Text>
                <ButtonGroup toggle className="mb-2">
                    <Button
                        variant={activeButton === 1 ? 'primary' : 'outline-primary'}
                        id="button-1"
                        onClick={() => handleButtonClick(1)}
                        disabled={activeButton !== null && activeButton !== 1}
                    >
                        Кнопка 1
                    </Button>
                    <Button
                        variant={activeButton === 2 ? 'primary' : 'outline-primary'}
                        id="button-2"
                        onClick={() => handleButtonClick(2)}
                        disabled={activeButton !== null && activeButton !== 2}
                    >
                        Кнопка 2
                    </Button>
                    {/* Аналогично для остальных кнопок */}
                </ButtonGroup>
            </Card.Body>
        </Card>
    );
}

export default ServerItem;
