import {useState} from "react";
import React from 'react';
import Card from 'react-bootstrap/Card';
import {ButtonGroup} from "react-bootstrap";
import {ToggleButton} from "react-bootstrap";
import '../Styles/ServerList.css';

function ServerItem({ item, buttonNames }) {
    const { name, ipAddress, port } = item;
    const [radioValue, setRadioValue] = useState(buttonNames[0]);

    const handleRadioChange = (value) => {
        setRadioValue(value);
    };
    return (
        <Card style={{ width: '31rem' }} >
            <Card.Title className="m-3">{item.name}</Card.Title>
            <Card.Subtitle className="mx-3">{item.ipAddress}:{item.port}</Card.Subtitle>
            <Card.Body>
                <ButtonGroup>
                    {buttonNames.map((buttonName, index) => (
                        <ToggleButton
                            key={index}
                            id={`radio-${index}`}
                            type="radio"
                            variant="outline-success"
                            name="radio"
                            value={buttonName}
                            checked={radioValue === buttonName}
                            onChange={(e) => handleRadioChange(e.currentTarget.value)}
                        >
                            {buttonName}
                        </ToggleButton>
                    ))}
                </ButtonGroup>
            </Card.Body>
        </Card>
    );
}

export default ServerItem;
