import React, { useState } from 'react';
import ListGroup from 'react-bootstrap/ListGroup';
import Button from 'react-bootstrap/Button';
import Form from 'react-bootstrap/Form';
import Card from 'react-bootstrap/Card';
import '../Styles/ServerList.css';

function ServerList({ items, onDelete, setItems }) {
    const [selectedItems, setSelectedItems] = useState([]);
    const [expandedItem, setExpandedItem] = useState(null);
    const [selectMode, setSelectMode] = useState(false);

    const handleSelectItem = (item) => {
        if (expandedItem === item) {
            setExpandedItem(null);
        } else {
            setExpandedItem(item);
        }
    };

    const handleDeleteSelected = () => {
        const updatedItems = items.filter(
            (item) => !selectedItems.includes(item)
        );
        onDelete(updatedItems);
        setSelectedItems([]);
        setItems(updatedItems);
        setSelectMode(false);
        localStorage.setItem('items', JSON.stringify(updatedItems));
    };

    const handleCancelDelete = () => {
        setSelectedItems([]);
        setSelectMode(false);
    };

    if (items.length === 0) {
        return (
            <Card>
                <Card.Body>
                    <Card.Title>Список элементов</Card.Title>
                    <Card.Text className="text-center">Список пуст</Card.Text>
                </Card.Body>
            </Card>
        );
    }

    return (
        <Card>
            <Card.Body>
                <Card.Title>Список элементов</Card.Title>

                <ListGroup>
                    {items.map((item, index) => (
                        <ListGroup.Item
                            key={index}
                            ip={item.ipAddress}
                            port={item.port}
                            className={`d-flex align-items-center ${
                                expandedItem === item ? 'expanded' : ''
                            }`}

                            action onClick={() => handleSelectItem(item)}
                        >
                            {selectMode && (
                                <Form.Check
                                    type="checkbox"
                                    checked={selectedItems.includes(item)}
                                    onChange={() => handleSelectItem(item)}
                                    onClick={(e) => e.stopPropagation()} // Prevent card expansion on checkbox click
                                />
                            )}
                            <div className={`mx-auto ${expandedItem === item ? 'expanded-card-width' : ''}`}>
                                {expandedItem === item ? (
                                    <Card>
                                        <Card.Body>
                                            <Card.Title><strong>{item.name}</strong></Card.Title>
                                            <Card.Text>
                                                <strong>IP:</strong>""""
                                                {item.ipAddress}
                                                <br/>
                                                <strong>Port:</strong> {item.port}
                                            </Card.Text>
                                        </Card.Body>
                                    </Card>
                                ) : (
                                    item.name
                                )}
                            </div>
                        </ListGroup.Item>
                    ))}
                </ListGroup>
                <br />
                {selectMode ? (
                    <div className="d-flex justify-content-between mb-3">
                        <Button variant="outline-danger" onClick={handleDeleteSelected}>
                            Удалить выбранные
                        </Button>
                        <Button variant="outline-secondary" onClick={handleCancelDelete}>
                            Отмена
                        </Button>
                    </div>
                ) : (
                    <Button variant="outline-primary" onClick={() => setSelectMode(true)}>
                        Выбрать
                    </Button>
                )}
            </Card.Body>
        </Card>
    );
}

export default ServerList;
