import React, { useContext, useState } from 'react';
import Card from 'react-bootstrap/Card';
import Button from 'react-bootstrap/Button';
import ButtonGroup from 'react-bootstrap/ButtonGroup';
import ToggleButton from 'react-bootstrap/ToggleButton';
import ServerContext from './ServerContext';
import { Col, Row } from 'react-bootstrap';

function ServerList() {
    const { servers, deleteServer, editServer } = useContext(ServerContext);
    const [selectedOption, setSelectedOption] = useState('');

    const handleDelete = (server) => {
        deleteServer(server);
    };

    const handleEdit = (server) => {
        editServer(server);
    };

    const handleOptionChange = (option) => {
        setSelectedOption(option);
        // You can perform any other logic or update the server data here based on the selected option
        // For example, you can update the 'server' object with the selected option value
    };

    return (
        <Row xs={1} md={2} lg={4} className="g-4">
            {servers.map((server, index) => (
                <Col key={index}>
                    <Card key={server.ipAddress} style={{ height: '30rem', width: '20rem', marginRight: '10px' }}>
                        <Card.Body>
                            <Card.Title>{server.name}</Card.Title>
                            <Card.Text>
                                <strong>IP Address:</strong> {server.ipAddress}
                                <br />
                                <strong>Port:</strong> {server.port}
                            </Card.Text>
                            <ButtonGroup toggle>
                                {[1, 2, 3, 4, 5, 6].map((option) => (
                                    <ToggleButton
                                        key={option}
                                        type="radio"
                                        variant="outline-primary"
                                        name={`radio-group-${index}`}
                                        value={`Option ${option}`}
                                        checked={selectedOption === `Option ${option}`}
                                        onChange={() => handleOptionChange(`Option ${option}`)}
                                    >
                                        Вывод {option}
                                    </ToggleButton>
                                ))}
                            </ButtonGroup>
                            <Button className="me-1" variant="outline-danger" onClick={() => handleDelete(server)}>
                                Удалить
                            </Button>
                            <Button className="me-1" variant="outline-primary" onClick={() => handleEdit(server)}>
                                Изменить
                            </Button>
                        </Card.Body>
                    </Card>
                </Col>
            ))}
        </Row>
    );
}

export default ServerList;
