import React, { useState } from 'react';
import Card from 'react-bootstrap/Card';
import { ButtonGroup } from 'react-bootstrap';
import { ToggleButton } from 'react-bootstrap';
import '../Styles/ServerList.css';

function ServerItem({ item }) {
    const { name, ipAddress, port } = item;
    const [radioValue, setRadioValue] = useState('1');

    const handleRadioChange = (event) => {
        setRadioValue(event.currentTarget.value);
    };

    return (
        <Card style={{ width: '31rem' }}>
            <Card.Title className="m-3">{name}</Card.Title>
            <Card.Subtitle className="mx-3">{ipAddress}:{port}</Card.Subtitle>
            <Card.Body>
                <ButtonGroup>
                    <ToggleButton
                        id={`radio-${item.id}`}
                        type="radio"
                        variant="outline-success"
                        name={`radio-${item.id}`}
                        value="1"
                        checked={radioValue === '1'}
                        onChange={handleRadioChange}
                    >
                        Порт 1
                    </ToggleButton>
                    <ToggleButton
                        id={`radio-${item.id}`}
                        type="radio"
                        variant="outline-success"
                        name={`radio-${item.id}`}
                        value="2"
                        checked={radioValue === '2'}
                        onChange={handleRadioChange}
                    >
                        Порт 2
                    </ToggleButton>
                    {/* Добавьте остальные кнопки по аналогии */}
                </ButtonGroup>
            </Card.Body>
        </Card>
    );
}

export default ServerItem;
