import React, { useState } from 'react';
import Button from 'react-bootstrap/Button';
import Col from 'react-bootstrap/Col';
import Form from 'react-bootstrap/Form';
import InputGroup from 'react-bootstrap/InputGroup';
import Row from 'react-bootstrap/Row';
import { Card, FloatingLabel } from 'react-bootstrap';

function AddDevice({ onAdd }) {
    const [name, setName] = useState('');
    const [ipAddress, setIPAddress] = useState('');
    const [port, setPort] = useState('');
    const [data, setData] = useState([]);
    const [ipError, setIpError] = useState('');

    const handleSubmit = (event) => {
        event.preventDefault();

        const isValidIP = validateIPAddress(ipAddress);

        if (!isValidIP) {
            setIpError('Неправильный формат IP-адреса');
            return;
        }

        const isNewIP = !data.some((item) => item.ipAddress === ipAddress);

        if (!isNewIP) {
            setIpError('Такой IP-адрес уже существует');
            return;
        }

        const newData = {
            name: name,
            ipAddress: ipAddress,
            port: port
        };

        onAdd(newData);
        setData([...data, newData]);
        setName('');
        setIPAddress('');
        setIpError('');
        setPort('');
    };

    const validateIPAddress = (ip) => {
        const ipParts = ip.split('.');
        if (ipParts.length !== 4) {
            return false;
        }
        for (let i = 0; i < 4; i++) {
            const part = parseInt(ipParts[i]);
            if (isNaN(part) || part < 0 || part > 255) {
                return false;
            }
        }
        return true;
    };

    return (
        <Card>
            <Card.Body>
                <Form>
                    <Row>
                        <Col xs="auto">
                            <Form.Label htmlFor="name" visuallyHidden>
                                Name
                            </Form.Label>
                            <Form.Control
                                type="text"
                                id="name"
                                className="mb-2 "
                                value={name}
                                onChange={(e) => setName(e.target.value)}
                                placeholder="Имя"
                            />
                        </Col>
                        <Col xs="auto">
                            <Form.Label htmlFor="ipAdress" visuallyHidden>
                                IP
                            </Form.Label>
                            <InputGroup className="mb-2">
                                <Form.Control
                                    className={`mb-2 ${ipError ? 'is-invalid' : ''}`}
                                    type="ip"
                                    id="ipAdress"
                                    value={ipAddress}
                                    onChange={(e) => {
                                        setIPAddress(e.target.value);
                                        const isValidIP = validateIPAddress(e.target.value);
                                        setIpError(isValidIP ? '' : 'Неправильный формат IP-адреса');
                                    }}
                                    placeholder="IP-Адрес"
                                />
                                <Form.Control.Feedback type="invalid">{ipError}</Form.Control.Feedback>
                            </InputGroup>
                        </Col>
                        <Col xs="auto">
                            <Form.Label htmlFor="port" visuallyHidden>
                                Port
                            </Form.Label>
                            <Form.Control
                                type="text"
                                id="port"
                                className="mb-2 "
                                value={port}
                                onChange={(e) => setPort(e.target.value)}
                                placeholder="Порт"
                            />
                        </Col>
                        <Col xs="auto">
                            <Button type="submit" className="mb-2" onClick={handleSubmit}>
                                Добавить
                            </Button>
                        </Col>
                    </Row>
                </Form>
            </Card.Body>
        </Card>
    );
}

export default AddDevice;
