import React, {useState} from 'react';
import Button from 'react-bootstrap/Button';
import Form from 'react-bootstrap/Form';
import Card from 'react-bootstrap/Card';
import Nav from 'react-bootstrap/Nav';
import Tab from 'react-bootstrap/Tab';
import Row from 'react-bootstrap/Row';
import Col from 'react-bootstrap/Col';
import ServerItem from "./ServerItem";
import '../Styles/ServerList.css';
function ServerList({items, onDelete, setItems}) {
    const [selectedItems, setSelectedItems] = useState([]);
    const [selectMode, setSelectMode] = useState(false);
    const [activeTab, setActiveTab] = useState(0);

    const handleSelectItem = (item) => {
        const isSelected = selectedItems.includes(item);
        setSelectedItems(isSelected
            ? selectedItems.filter((selectedItem) => selectedItem !== item)
            : [...selectedItems, item]
        );
    };

    const handleDeleteSelected = () => {
        const updatedItems = items.filter((item) => !selectedItems.includes(item));
        onDelete(updatedItems);
        setSelectedItems([]);
        setItems(updatedItems);
        setSelectMode(false);
        localStorage.setItem('items', JSON.stringify(updatedItems));
    };


    const handleCancelDelete = () => {
        setSelectedItems([]);
        setSelectMode(false);
    };

    if (items.length === 0) {
        return (<Card>
            <Card.Body>
                <Card.Title>Список элементов</Card.Title>
                <Card.Text className="text-center">Список пуст</Card.Text>
            </Card.Body>
        </Card>);
    }

    return (
        <Tab.Container activeKey={activeTab} onSelect={setActiveTab}>
        <Row>
            <Col sm={3}>
                <Card  className="justify-content-center ">
                    <Card.Body>
                        <Card.Title>Список элементов</Card.Title>
                        <Nav justify variant="pills" className="flex-column">
                            {items.map((item, index) => (<Nav.Item key={index} ip={item.ipAddress} port={item.port}
                                                                   className="d-flex align-items-center">
                                {selectMode && (<Form.Check
                                    className="mx-2"
                                    type="checkbox"
                                    checked={selectedItems.includes(item)}
                                    onChange={() => handleSelectItem(item)}
                                />)}
                                <Nav.Link eventKey={`server-${index}`} className="mx-auto">{item.name}</Nav.Link>

                            </Nav.Item>))}
                        </Nav>
                    </Card.Body>
                    <Card.Footer>
                        {selectMode ? (
                            <div className="d-flex justify-content-between mb-3">
                                <Button variant="outline-danger" onClick={handleDeleteSelected}>
                                    Удалить выбранные
                                </Button>
                                <Button variant="outline-secondary" onClick={handleCancelDelete}>
                                    Отмена
                                </Button>
                            </div>
                        ) : (
                            <Button variant="outline-primary" onClick={() => setSelectMode(true)}>
                                Выбрать
                            </Button>)
                        }
                    </Card.Footer >
                </Card>


            </Col>

            <Col>
                <Tab.Content  className="wd-100">
                    {items.map((item, index) => (
                        <Tab.Pane eventKey={`server-${index}`} ip={item.ipAddress} port={item.port} className="d-flex align-items-center">
                            <Nav.Link eventKey={`server-${index}`} className="mx-auto">
                                <ServerItem item={item} />
                            </Nav.Link>
                        </Tab.Pane>))}
                </Tab.Content>
            </Col>
        </Row>

        <Row className="my-1">
            <Col sm={3}>



            </Col>
        </Row>
    </Tab.Container>)
}


export default ServerList;
