import React, { useState } from 'react';
import ListGroup from 'react-bootstrap/ListGroup';
import Button from 'react-bootstrap/Button';
import Form from 'react-bootstrap/Form';
import Card from 'react-bootstrap/Card';

function ServerList({ items, onDelete, setItems }) {
    const [selectedItems, setSelectedItems] = useState([]);
    const [selectMode, setSelectMode] = useState(false);
    const [selectedCard, setSelectedCard] = useState(null); // Добавленная переменная

    const handleSelectItem = (item) => {
        const isSelected = selectedItems.includes(item);
        let updatedSelectedItems;

        if (isSelected) {
            updatedSelectedItems = selectedItems.filter(
                (selectedItem) => selectedItem !== item
            );
        } else {
            updatedSelectedItems = [...selectedItems, item];
        }

        setSelectedItems(updatedSelectedItems);
        setSelectedCard(null); // Добавленная строка
    };

    const handleDeleteSelected = () => {
        const updatedItems = items.filter(
            (item) => !selectedItems.includes(item)
        );
        onDelete(updatedItems);
        setSelectedItems([]);
        setItems(updatedItems);
        setSelectMode(false);
        localStorage.setItem('items', JSON.stringify(updatedItems));
    };

    const handleCancelDelete = () => {
        setSelectedItems([]);
        setSelectMode(false);
    };

    if (items.length === 0) {
        return (
            <Card>
                <Card.Body>
                    <Card.Title>Список элементов</Card.Title>
                    <Card.Text className="text-center">Список пуст</Card.Text>
                </Card.Body>
            </Card>
        );
    }

    return (
        <Card>
            <Card.Body>
                <Card.Title>Список элементов</Card.Title>

                <ListGroup>
                    {items.map((item, index) => (
                        <ListGroup.Item
                            key={index}
                            ip={item.ipAddress}
                            port={item.port}
                            className="d-flex align-items-center"
                        >
                            {selectMode && (
                                <Form.Check
                                    type="checkbox"
                                    checked={selectedItems.includes(item)}
                                    onChange={() => handleSelectItem(item)}
                                />
                            )}
                            <div className="mx-auto">{item.name}</div>
                        </ListGroup.Item>
                    ))}
                </ListGroup>

                {/* Остальной код */}
            </Card.Body>
        </Card>
    );
}

export default ServerList;
