import React, { useState } from 'react';
import Card from 'react-bootstrap/Card';
import ToggleButton from 'react-bootstrap/ToggleButton';
import ButtonGroup from 'react-bootstrap/ButtonGroup';
import axios from 'axios';

function ServerItem({ item }) {
    const [activeButton, setActiveButton] = useState(null);

    const handleButtonClick = async (buttonValue) => {
        if (buttonValue === activeButton) {
            setActiveButton(null);
        } else {
            try {
                const response = await axios.get(`http://${item.ipAddress}:${item.port}/api/${buttonValue}`);
                console.log(`Отправлен гет-запрос для элемента ${item.name}, кнопка ${buttonValue}`);
                console.log('Ответ:', response.data);
            } catch (error) {
                console.error('Ошибка при выполнении гет-запроса:', error);
            }

            setActiveButton(buttonValue);
        }
    };

    return (
        <Card>
            <Card.Body>
                <Card.Title>{item.name}</Card.Title>
                <Card.Text>
                    IP адрес: {item.ipAddress}
                    <br />
                    Порт: {item.port}
                </Card.Text>
                <ButtonGroup toggle className="mb-2">
                    <ToggleButton
                        type="radio"
                        variant="primary"
                        value={1}
                        checked={activeButton === 1}
                        onChange={() => handleButtonClick(1)}
                    >
                        Кнопка 1
                    </ToggleButton>
                    <ToggleButton
                        type="radio"
                        variant="primary"
                        value={2}
                        checked={activeButton === 2}
                        onChange={() => handleButtonClick(2)}
                    >
                        Кнопка 2
                    </ToggleButton>
                    {/* Аналогично для остальных кнопок */}
                </ButtonGroup>
            </Card.Body>
        </Card>
    );
}

export default ServerItem;
