import { useState } from 'react';
import Card from 'react-bootstrap/Card';
import { ButtonGroup, ToggleButton } from 'react-bootstrap';

function ServerItem({ item, buttonNames }) {
    const { name, ipAddress, port } = item;
    const [radioValue, setRadioValue] = useState('');

    const handleRadioChange = (value) => {
        setRadioValue(value);
    };

    return (
        <Card style={{ width: '31rem' }}>
            <Card.Title className="m-3">{name}</Card.Title>
            <Card.Subtitle className="mx-3">
                {ipAddress}:{port}
            </Card.Subtitle>
            <Card.Body>
                <ButtonGroup>
                    {buttonNames.map((name, index) => (
                        <ToggleButton
                            key={index}
                            id={`radio-${index}`}
                            type="radio"
                            variant="outline-success"
                            name="radio"
                            value={name}
                            checked={radioValue === name}
                            onChange={(e) => handleRadioChange(e.currentTarget.value)}
                        >
                            {name}
                        </ToggleButton>
                    ))}
                </ButtonGroup>
            </Card.Body>
        </Card>
    );
}

export default ServerItem;
