import React, { useState } from 'react';
import ListGroup from 'react-bootstrap/ListGroup';
import Button from 'react-bootstrap/Button';
import Form from 'react-bootstrap/Form';
import Card from 'react-bootstrap/Card';

function ServerList({ items, onDeleteItem }) {
    const [selectedItems, setSelectedItems] = useState([]);
    const [selectMode, setSelectMode] = useState(false);

    const handleSelectItem = (item) => {
        const isSelected = selectedItems.includes(item);
        let updatedSelectedItems;

        if (isSelected) {
            updatedSelectedItems = selectedItems.filter((selectedItem) => selectedItem !== item);
        } else {
            updatedSelectedItems = [...selectedItems, item];
        }

        setSelectedItems(updatedSelectedItems);
    };

    const handleDeleteSelected = () => {
        const updatedItems = items.filter((item) => !selectedItems.includes(item));
        updatedItems.forEach((item) => onDeleteItem(item));
        setSelectedItems([]);
        setSelectMode(false);
    };
    if (items.length === 0) {
        return (
            <Card>
                <Card.Body>
                    <Card.Title>Список элементов</Card.Title>
                    <Card.Text className="text-center">Список пуст</Card.Text>
                </Card.Body>
            </Card>
        );
    }
    return (
        <div>
            {selectMode ? (
                <div className="d-flex mb-2">
                    <Button variant="outline-danger" onClick={handleDeleteSelected} className="me-2">
                        Удалить выбранные
                    </Button>
                    <Button variant="outline-secondary" onClick={() => setSelectMode(false)}>
                        Отмена
                    </Button>
                </div>
            ) : (
                <Button variant="outline-primary" onClick={() => setSelectMode(true)} className="mb-2">
                    Выбрать
                </Button>
            )}


            ) : (
                <ListGroup>
                    {items.map((item,
                                index) => (
                        <ListGroup.Item key={index}>
                            {selectMode && (
                                <Form.Check
                                    type="checkbox"
                                    checked={selectedItems.includes(item)}
                                    onChange={() => handleSelectItem(item)}
                                />
                            )}
                            <span className="ms-2 me-auto">{item.name}</span>
                        </ListGroup.Item>
                    ))}
                </ListGroup>
            )}
        </div>
    );
}

export default ServerList;
