import React, { useState } from 'react';
import Card from 'react-bootstrap/Card';
import ButtonGroup from 'react-bootstrap/ButtonGroup';
import ToggleButton from 'react-bootstrap/ToggleButton';

function ServerItem({ item }) {
    const { name, ipAddress, port } = item;
    const [radioValue, setRadioValue] = useState('1');

    const handleRadioChange = (value) => {
        setRadioValue(value);
    };

    return (
        <Card style={{ width: '31rem' }}>
            <Card.Title className="m-3">{name}</Card.Title>
            <Card.Subtitle className="mx-3">{ipAddress}:{port}</Card.Subtitle>
            <Card.Body>
                <ButtonGroup>
                    <ToggleButton
                        type="radio"
                        variant="outline-success"
                        name={`radio-${name}`}
                        value="1"
                        checked={radioValue === '1'}
                        onChange={() => handleRadioChange('1')}
                    >
                        Порт 1
                    </ToggleButton>
                    <ToggleButton
                        type="radio"
                        variant="outline-success"
                        name={`radio-${name}`}
                        value="2"
                        checked={radioValue === '2'}
                        onChange={() => handleRadioChange('2')}
                    >
                        Порт 2
                    </ToggleButton>
                    {/* Добавьте другие кнопки ToggleButton здесь */}
                </ButtonGroup>
            </Card.Body>
        </Card>
    );
}

export default ServerItem;
