import React, {useState, useEffect} from 'react';
import ServerList from './components/ServerList';
import AddDevice from './components/AddDevice';
import Header from './components/Header';
import Container from 'react-bootstrap/Container';
import Row from 'react-bootstrap/Row';
import Col from 'react-bootstrap/Col';



function App() {
    const [items, setItems] = useState([]);

    useEffect(() => {
        // Загрузка данных из localStorage при загрузке приложения
        const savedItems = JSON.parse(localStorage.getItem('items'));
        if (savedItems) {
            setItems(savedItems);
        }
    }, []);

    const handleAddItem = (newItem) => {
        const updatedItems = [...items, newItem];
        setItems(updatedItems);
        // Сохранение данных в localStorage при добавлении элемента
        localStorage.setItem('items', JSON.stringify(updatedItems));
    };

    const handleDeleteItem = (index) => {
        const updatedItems = [...items];
        updatedItems.splice(index, 1);
        setItems(updatedItems);
        // Сохранение данных в localStorage при удалении элемента
        localStorage.setItem('items', JSON.stringify(updatedItems));
    };

    return (
        <div className="App">
            <Header/>
            <Container fluid={"xl"}>
                <Row className="my-1">
                    <Col className="w-auto">
                        <AddDevice onAdd={handleAddItem} items={items}/>
                    </Col>
                </Row>

                        <ServerList items={items} onDelete={handleDeleteItem} setItems={setItems}/>



            </Container>
        </div>
    );
}

export default App;
